import React, {useState, useEffect} from 'react';
import {Button, Checkbox, Icon, Input, Loader} from 'semantic-ui-react';
import validator from 'validator';

import './Todo.css';

function TodoEngine(){
    // Initialize our state variables
    const [todo, setTodo] = useState('');
    const [loading, setLoading] = useState(false);
    const [todos, getTodos] = useState([]);

    // Save todo item
    const saveTodo = (e)=>{
        e.preventDefault();
        const savedItems = JSON.parse(window.localStorage.getItem('todo'));
        let items=[];

        // Validate to see if todo isnt an empty task
        if(validator.isAlphanumeric(todo)){
            if(savedItems === null){
                items = [
                    {
                        id:Math.floor(Math.random(99999)*232234),
                        todo,
                        completed:'false'
                    }];
                    window.localStorage.setItem('todo', JSON.stringify(items));   
                    setTodo("");       
            }else{
                items = [
                        {
                            id:Math.floor(Math.random(99999)*2342342),
                            todo,
                            completed:'false'
                        },
                        ...savedItems,
                    ];      
            }
        } 
        else{
            alert('Insert a task')
        }

        // Save items
        window.localStorage.setItem('todo', JSON.stringify(items));   
        setTodo("");  

    }

    function loadItems(){
        setLoading(true);
        const items = JSON.parse(window.localStorage.getItem('todo'));
        getTodos(items);
        setLoading(false);
    }

    function clearItems(){
        window.localStorage.removeItem('todo');
        getTodos([]);
    }

    function removeTodo(todo){
        const items = todos.filter(item => item.id !== todo.id );
        window.localStorage.setItem('todo', JSON.stringify(items));
        loadItems(todos)

    }

    function taskCompleted(todo){
        const items = todos.map((item,idx,arr) => {
            if(item.id === todo.id){
                item.completed = 'true';
            }
            return item
        });
        window.localStorage.setItem('todo', JSON.stringify(items));
        loadItems(todos)  
    }

    //useEffect will load only once when component is mounted
    useEffect(()=>{
        loadItems(todos);
    },[todo])

    return (
        <div>
        { (loading) ? <Loader active inline='centered' style={{marginTop:'5%;'}} /> :
            <div>
                <form>                
                    <Input 
                        value={todo}
                        onChange={(e)=> setTodo(e.target.value)}
                        placeholder="Please insert a task..."
                        style={{width:'70%',marginBottom:'1%'}}
                    />
                    <Button 
                        inverted color="blue" 
                        onClick={e => saveTodo(e)}>Save</Button>
                </form>   
                <div className="listContainer">
                    <Button inverted color="red" onClick={()=>clearItems()} style={{float:'right',marginBottom:'-3%'}}>Clear</Button>
                    <ul>
                    <h3>Todo List</h3>

                        {
                            (todos === null || todos.length === 0) ? <h5>There are currently no items at the moment.</h5>  : 
                                todos.map((item,idx)=>
                                    <li key={idx} className="list">
                                        <Checkbox 
                                            onClick={(e)=> taskCompleted(item)} 
                                            checked={ (item.completed === 'true' ) ? 'true': '' }
                                            toggle
                                            />
                                        <p>{item.todo}</p>     
                                        <p className="completed">Completed: <b> {item.completed} </b> </p>
                                        <Icon name='trash' onClick={(e) => removeTodo(item)}/>Completed
                                    </li>
                                ) 
                        }
                    </ul>
                </div>
            </div>

            }
        </div>
    )
}

export default TodoEngine;
