import React, {useState, useEffect} from 'react';


function TodoEngine(){
    // Initialize our state variables
    const [todo, setTodo] = useState('');
    // const [loading, setLoading] = useState(false);
    const [todos, getTodos] = useState([])

    // Save todo item
    const saveTodo = (e)=>{
        e.preventDefault();
        const savedItems = JSON.parse(window.localStorage.getItem('todo'));
        let items=[];
        if(savedItems === null){
            items = [
                {
                    id:Math.floor(Math.random(99999*332)*224),
                    todo,
                    completed:false
                }];
        }else{
            items = [
                    {
                        id:Math.floor(Math.random(99999*332)*224),
                        todo,
                        completed:false
                    },
                    ...savedItems,
                ];            
        }

        window.localStorage.setItem('todo', JSON.stringify(items));
        setTodo("");

    }
    function loadItems(){
        const items = JSON.parse(window.localStorage.getItem('todo'));
        getTodos(items)
    }


    //useEffect will load only once when component is mounted
    useEffect(()=>{
        loadItems(todos);
    },[todo])

    return (
        <div>
            <h2>Mac Todo</h2>
            <form>
                <h3>Insert a task...</h3>
                <input 
                    value={todo}
                    onChange={(e)=> setTodo(e.target.value)}
                />
                <button onClick={e => saveTodo(e)}>Save</button>
            </form>
            <hr />
            <ul>
                {
                    (todos === null) ? <h4>There are currently no items at the moment.</h4>  : 
                        <ul>
                        {todos.map((item,idx)=>
                            <div key={idx}>
                                <p>{item.todo}</p>        
                                <p>{item.completed}</p>
                            </div>
                        )} 
                    </ul>
                }
            </ul>
        </div>
    )
}

export default TodoEngine;
