function TodoEngine(){
    // Initialize our state variables
    const [todo, setTodo] = useState('');
    // const [loading, setLoading] = useState(false);
    const [todos, getTodos] = useState([])

    // Save todo item
    const saveTodo = (e)=>{
        e.preventDefault();
        console.log(todo);
        const savedItems = window.localStorage.getItem('todo');
            let items = [{
                id:Math.random(10*32)*224,
                todo,
                completed:false
            }];

        window.localStorage.setItem('todo', JSON.stringify(items));

    }


    return (
        <div>
            <h2>Mac Todo</h2>
            <form>
                <h3>Insert a task...</h3>
                <input 
                    value={todo}
                    onChange={(e)=> setTodo(e.target.value)}
                />
                <button onClick={e => saveTodo(e)}>Save</button>
            </form>
            <hr />
            <ul>
                {
                    (todos.length === 0 || !todos) ? <h2>THer are none</h2>  : <h2> {console.log(todos)} </h2> 
                }
            </ul>
        </div>
    )
}

export default TodoEngine;
