import React, {useState, useEffect} from 'react';


function TodoEngine(){
    // Initialize our state variables
    const [todo, setTodo] = useState("");
    const [loading, setLoading] = useState(false);

    // Save todo item
    const saveTodo = (e)=>{
        e.preventDefault();
        console.log(todo);
        const savedItems = window.localStorage.getItems('todo');
        console.log(savedItems)
        let items = [];

        window.localStorage.setItem(items)

    }

    return (
        <div>
            <h2>Mac Todo</h2>
            <form>
                <h3>Insert a task...</h3>
                <input 
                    value={todo}
                    onChange={(e)=> setTodo(e.target.value)}
                />
                <button onClick={e => saveTodo(e)}>Save</button>
            </form>
        </div>
    )
}

export default TodoEngine;
