import React, {useState, useEffect} from 'react';


function TodoEngine(){
    // Initialize our state variables
    const [todo, setTodo] = useState('');
    // const [loading, setLoading] = useState(false);
    const [todos, getTodos] = useState([])

    // Save todo item
    const saveTodo = (e)=>{
        e.preventDefault();
        const savedItems = JSON.parse(window.localStorage.getItem('todo'));
        let items;

        if(savedItems.length === 0){
            items = [
                {
                    id:Math.floor(Math.random(99999*332)*224),
                    todo,
                    completed:false
                }];
        }
        items = [
                ...savedItems,
                {
                    id:Math.floor(Math.random(99999*332)*224),
                    todo,
                    completed:false
                }];

        window.localStorage.setItem('todo', JSON.stringify(items));

    }
    function loadItems(){
        console.log('loading items...');
        const items = JSON.parse(window.localStorage.getItem('todo'));
        console.log(items)
    }


    // useEffect will load only once when component is mounted
    useEffect(()=>{
        loadItems()
    })

    return (
        <div>
            <h2>Mac Todo</h2>
            <form>
                <h3>Insert a task...</h3>
                <input 
                    value={todo}
                    onChange={(e)=> setTodo(e.target.value)}
                />
                <button onClick={e => saveTodo(e)}>Save</button>
            </form>
            <hr />
            <ul>
                {
                    (todos.length === 0 || !todos) ? <h2>THer are none</h2>  : <h2> {console.log(todos)} </h2> 
                }
            </ul>
        </div>
    )
}

export default TodoEngine;
