import React, {useState, useEffect} from 'react';
import {Form} from 'react-bootstrap';

function TodoEngine(){
    // Initialize our state variables
    const [todo, setTodo] = useState('');
    // const [loading, setLoading] = useState(false);
    const [todos, getTodos] = useState([])

    // Save todo item
    const saveTodo = (e)=>{
        e.preventDefault();
        const savedItems = JSON.parse(window.localStorage.getItem('todo'));
        let items=[];
        if(savedItems === null){
            items = [
                {
                    id:Math.floor(Math.random(99999)*232234),
                    todo,
                    completed:'false'
                }];
        }else{
            items = [
                    {
                        id:Math.floor(Math.random(99999)*2342342),
                        todo,
                        completed:'false'
                    },
                    ...savedItems,
                ];            
        }

        window.localStorage.setItem('todo', JSON.stringify(items));
        setTodo("");

    }

    function loadItems(){
        const items = JSON.parse(window.localStorage.getItem('todo'));
        getTodos(items);
        console.log(items)
    }

    function clearItems(){
        window.localStorage.removeItem('todo');
        getTodos([]);
    }

    //useEffect will load only once when component is mounted
    useEffect(()=>{
        loadItems(todos);
    },[todo])

    return (
        <div>
            <button onClick={()=>clearItems()}>Clear</button>
            <Form>
                <h3>Insert a task...</h3>
                <input 
                    value={todo}
                    onChange={(e)=> setTodo(e.target.value)}
                />
                <Button variant="info" onClick={e => saveTodo(e)}>Save</Button>
            </Form>
            <hr />
            <ul>
                <h4>Todo List</h4>
                {
                    (todos === null) ? <h4>There are currently no items at the moment.</h4>  : 
                        todos.map((item,idx)=>
                            <li key={idx}>
                                <p>{item.id}</p>   
                                <p>{item.todo}</p>     
                                <p>{item.completed}</p>
                            </li>
                        ) 
                }
            </ul>
        </div>
    )
}

export default TodoEngine;
