import React, {useState, useEffect} from 'react';


function TodoEngine(){
    // Initialize our state variables
    const [todo, setTodo] = useState("");
    const [loading, setLoading] = useState(false);
    const [todos, getTodo] = useState([window.localStorage.getItem('todo')])

    // Save todo item
    const saveTodo = (e)=>{
        e.preventDefault();
        console.log(todo);
        const savedItems = window.localStorage.getItem('todo');
        console.log(savedItems);
        let items = [];

        window.localStorage.setItem('todo',items)

    }

    return (
        <div>
            <h2>Mac Todo</h2>
            <form>
                <h3>Insert a task...</h3>
                <input 
                    value={todo}
                    onChange={(e)=> setTodo(e.target.value)}
                />
                <button onClick={e => saveTodo(e)}>Save</button>
            </form>
            <hr />
            <ul>
                {
                    (todos.length > 0) ? <h2>todos.length</h2> : <h2>THer are none</h2>
                }
            </ul>
        </div>
    )
}

export default TodoEngine;
