import React, {useState, useEffect} from 'react';
import {Form,Button} from 'react-bootstrap';
import './Todo.css';

function TodoEngine(){
    // Initialize our state variables
    const [todo, setTodo] = useState('');
    // const [loading, setLoading] = useState(false);
    const [todos, getTodos] = useState([])

    // Save todo item
    const saveTodo = (e)=>{
        e.preventDefault();
        const savedItems = JSON.parse(window.localStorage.getItem('todo'));
        let items=[];
        if(savedItems === null){
            items = [
                {
                    id:Math.floor(Math.random(99999)*232234),
                    todo,
                    completed:'false'
                }];
        }else{
            items = [
                    {
                        id:Math.floor(Math.random(99999)*2342342),
                        todo,
                        completed:'false'
                    },
                    ...savedItems,
                ];            
        }

        window.localStorage.setItem('todo', JSON.stringify(items));
        setTodo("");

    }

    function loadItems(){
        const items = JSON.parse(window.localStorage.getItem('todo'));
        getTodos(items);
    }

    function clearItems(){
        window.localStorage.removeItem('todo');
        getTodos([]);
    }

    //useEffect will load only once when component is mounted
    useEffect(()=>{
        loadItems(todos);
    },[todo])

    return (
        <div>
            <h5>Insert a task...</h5>
            <form>                
                <input 
                    value={todo}
                    onChange={(e)=> setTodo(e.target.value)}
                />
                {/* <button className="saveBtn" onClick={e => saveTodo(e)}>Save</button> */}
                <button variant="outline-info" onClick={e => saveTodo(e)}>Info</button>{' '}
            </form>

            <div className="listContainer">
                <Button variant="outline-danger" onClick={()=>clearItems()} style={{float:'right',marginBottom:'-3%'}}>Clear</Button>
                <ul>
                <h6>Todo List</h6>

                    {
                        (todos === null) ? <h5>There are currently no items at the moment.</h5>  : 
                            todos.map((item,idx)=>
                                <li key={idx} className="list">
                                    <p>{item.todo}</p>     
                                    <p className>Completed:<b>{item.completed}</b></p>
                                </li>
                            ) 
                    }
                </ul>
            </div>
        </div>
    )
}

export default TodoEngine;
