import React, {useState, useEffect} from 'react';
import {Button, Checkbox, Icon, Input, Loader} from 'semantic-ui-react';
import validator from 'validator';
import './Todo.css';
import { render } from 'react-dom';


function SearchEngine(){
    // Initialize our state variables
    const [search, setSearch] = useState('');
    const [results, renderResults] = useState([]);

    // Search for an item via search bar
    function searchItem(value) {
        const regExp = new RegExp(value);
        console.log(value.match(regExp))
        if(todos !== null && todos.length !==0 ){
            todos.filter((item,idx) => {
                const isMatch = item.todo.match(regExp);
                console.log(isMatch);
                if(isMatch) {
                    renderResults(item.todo);
                }
            });            
        }
    }    

    // function renderResults(item){
    //     results = [item,...results]
    // }


    //useEffect will load only once when component is mounted
    // useEffect(()=>{

    // },[search]);

    return (
        <div>
            <Input 
                id="search_results"
                icon='search' 
                placeholder='Search...' 
                style={{float:'left',margin:'1%'}} 
                onChange={e => searchItem(e.target.value)} /> 

            <datalist id="search_results">
                {results.map((item,idx)=>
                    <option key = {idx}>
                        {item}
                    </option>                    

                )}
            </datalist>

        </div>
    )
}

export default SearchEngine;
